mapper.select:
SELECT ad.input_date AS input_date, ep.employment_type AS employment_type, ep.korean_name AS korean_name, dp.department_name AS department_name, po.position_name AS position_name, adt.attendance_type_name AS attendance_type_name, ad.start_date AS start_date, ad.end_date AS end_date, ad.attendance_days AS attendance_days, ad.amount AS amount, ad.summary AS summary FROM attendance ad JOIN employee ep ON ad.employee_id = ep.employee_id JOIN attendance_type adt ON ad.attendance_type_id = adt.attendance_type_id
JOIN department dp ON ep.department_id = dp.department_id JOIN position po ON ep.position_id = po.position_id
AND ad.input_date = #{inputDate}
AND ad.start_date BETWEEN #{startDate} AND #{endDate}
AND dp.department_name = #{departmentName}
AND ep.korean_name = #{koreanName}
AND adt.attendance_type_name = #{attendanceTypeName}
AND vt.vacation_type_name = #{vacationTypeName}
AND ag.attendance_group_name = #{attendanceGroupName}
AND ad.summary = #{summary}
