mapper.insert:
INSERT INTO certification (
certification_id,
employee_id, certification_name, acquisition_date,
issuing_organization, certification_number, remarks
) VALUES (
certification_seq.NEXTVAL, #{employeeId}, #{certificationName},
#{acquisitionDate},
#{issuingOrganization}, #{certificationNumber},
#{remarks}
)

INSERT INTO appointment (
appointment_id, employee_id,
appointment_type, appointment_date, department,
position,
position_type, remarks
) VALUES (
appointment_seq.NEXTVAL,
#{employeeId}, #{appointmentType}, #{appointmentDate}, #{department},
#{position}, #{positionType}, #{remarks}
)

INSERT INTO retirement (
retirement_id, employee_id, retirement_type, retirement_date,
retirement_reason, contact_after_retirement, retirement_pay
) VALUES (
retirement_seq.NEXTVAL, #{employeeId}, #{retirementType}, #{retirementDate},
#{retirementReason}, #{contactAfterRetirement}, #{retirementPay}
)
